casper.test.begin('Widget - Create doodle', function(test) {

    casper.start(configUtil.adminUI, function() {

        casper.echo('configUtil : ' + configUtil.adminUI);

        // Log in with admin user
        // userUtil.doLogIn(configUtil.adminUsername, configUtil.adminPassword);
        // uiUtil.openAdmin();

        // Verify casper is working fine
        casper.then(function() {
            casper.echo('# Verify casper is working fine', 'INFO');
            var test = casper.evaluate(function () {
                return 'toto';
            });
            this.test.assert(test === 'toto', 'Test est bien égal à toto');
        });

        // Log out with admin user
        userUtil.doLogOut();

    });

    casper.run(function() {
        test.done();
    });

});
